// components/errorRecovery/errorRecovery.js
const app = getApp()
const API_URL = 'https://xcx2.chinaplat.com/daoyou/'; //接口地址
Component({
  /**
   * 组件的属性列表
   */
  properties: {
    windowHeight: {
      type: Number,
      value: 1333
    }
  },

  /**
   * 组件的初始数据
   */
  data: {
    first: true,
    icons: [
      'data:image/jpeg;base64,',
      'data:image/jpeg;base64,'
    ],
    qiandaos: new Array(30),
    SignDays: 0,
    SignHeadImgs: [
      '/images/avatar.png',
      '/images/avatar.png',
      '/images/avatar.png',
      '/images/avatar.png',
      '/images/avatar.png',
      '/images/avatar.png'
    ],
    ksDate: 0 //还剩多少天
  },

  /**
   * 组件的方法列表
   */
  methods: {
    //隐藏弹框
    hideDialog() {
      this.setData({
        isShow: false
      })
    },
    //展示弹框
    showDialog() {
      let user = wx.getStorageSync('user');
      let zcode = user.zcode ? user.zcode : '';
      let token = user.token ? user.token : '';
      let self = this;
      let qiandaos = this.data.qiandaos;

      self.setData({
        isShow: true,
      })

      wx.showShareMenu({
        withShareTicket: true,
        success: function(e) {

        }
      })

      this.initQiandao();

      app.post(API_URL, "action=QianDao&zcode=" + zcode + "&token=" + token, false, false, "", "", false, self).then(res => {

        let result = res.data.data[0];
        let SignDays = result.SignDays;//连续签到
        let SignHeadImgs = result.SignHeadImg;
        let SignNums = result.SignNums;
        let SendJifen = result.SendJifen;
        let current = SignDays - 3 <= 0 ? 0 : SignDays - 3;
        console.log(result)

        if (SendJifen) {
          this.daka = this.selectComponent("#daka");
          this.daka.setData({
            jifen: SendJifen
          })
          this.daka.showDialog();
        }


        self.setData({
          SignDays: SignDays,
          SignHeadImgs: SignHeadImgs,
          SignNums: SignNums,
          current: current,
          isDaka: false,
          ksDate: result.ksDate
        })
      })

    },
    //根据连续签到天数和积分初始化签到数组
    initQiandao() {
      let qiandaos = this.data.qiandaos;
      for (let i = 0; i < qiandaos.length; i++) {
        qiandaos[i] = {};

        if ((i + 1) % 7 == 1) {
          qiandaos[i].jifen = 5;
        } else if ((i + 1) % 7 == 2) {
          qiandaos[i].jifen = 10;
        } else if ((i + 1) % 7 == 3) {
          qiandaos[i].jifen = 20;
        } else if ((i + 1) % 7 == 4) {
          qiandaos[i].jifen = 30;
        } else if ((i + 1) % 7 == 5) {
          qiandaos[i].jifen = 50;
        } else if ((i + 1) % 7 == 6) {
          qiandaos[i].jifen = 100;
        } else if ((i + 1) % 7 == 0) {
          qiandaos[i].jifen = 200;
        }
      }

      this.setData({
        qiandaos: qiandaos
      })
    },

    //toogle展示
    toogleDialog() {
      this.setData({
        isShow: !this.data.isShow
      })
    },
    //阻止事件冒泡
    stopBubbling: function(e) {},

    //点击了空地,让蒙版消失
    tapBlank: function(e) {
      this.setData({
        isShow: false
      })
    },

    //点击海报按钮
    _createHaibao: function(e) {
      let self = this;
      //生成海报
      this.setData({
        isShow: false
      })
      console.log(this.data.SignDays)
      self.triggerEvent("createHaibao", { SignDays: this.data.SignDays});
    }
  }

})